import sqlite3

# criando conexao
conn = sqlite3.connect('escola.db')
# ele vai intermediar as execusões no banco
cursor = conn.cursor()

# Relacionais
# MYSQL, MariaDB, Postgres, SQL SERVER, Microsoft SQL
# DDL -> Data definition Language -> Linguagem de definição de dados
# Comando estruturais

# Criar uam tabela
cursor.execute('CREATE TABLE IF NOT EXISTS alunos (id INTEGER PRIMARY KEY, nome TEXT, idade INTEGER)')                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       

# DML -> Data Manipulation Languege -> Linguagem de Manupulação de Dados
# CRUD -> CREATE -> Criar novas informações
cursor.execute('INSERT INTO alunos (nome, idade) VALUES ("Mariana", 17)')
# Execultar a consulta
conn.commit()

print("\n Lista de Alunos: ")
cursor.execute('SELECT nome FROM alunos')
for linha in cursor.fetchall():
    print(linha)